import { fetchItems } from "../Services/api";
export default {
  namespace: "posts",
  state: {
    items:[]
  },
  reducers: {
    saveItems(state, { payload }) {
      
      return { ...state, items:payload };
    }
  },
  effects: {
    *getData(action, { call, put }) {
      const items = yield call(fetchItems);
      yield put({ type: "saveItems", payload: items.data });
    }
  },
  subscriptions: {}
};
