import React, { Component } from "react";
import { connect } from "dva";
import PostItems from "../Components/postitems";

class posts extends Component {
  componentDidMount(){
    this.props.dispatch({ type: "posts/getData" })
  }
  render() {
    return (
      <React.Fragment>
        <h1>Posts</h1>
        <div>
          {this.getDataState && <h2>{this.props.getDataState.map((post)=><PostItems id={post.id} post={post}></PostItems>)}</h2>}
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  return {
    getData: state.posts.getData,
    getDataState:state.posts.items
  };
};

export default connect(mapStateToProps)(posts);
